package com.github.hirsivaja.ip.payload

import com.github.hirsivaja.ip.IpProtocols.Icmp
import com.github.hirsivaja.ip.header.{Ipv4Header, Ipv6Header}
import com.github.hirsivaja.ip.{IpPacket, Ipv4Packet, Ipv6Packet}
import com.github.hirsivaja.ip.payload.Generic
import org.scalatest.flatspec.AnyFlatSpec

import java.nio.ByteBuffer
import java.util.HexFormat

class IpPayloadSpec extends AnyFlatSpec {
  it should "create a generic IPv4 payload" in {
    val genericString = ""
    val in = ByteBuffer.wrap(HexFormat.of.parseHex(genericString))
    val header = Ipv4Header.decode(in, true)
    val payload = Ipv4Payload.decode(in, true, header)
    val packet: IpPacket = new Ipv4Packet(header, payload)
    val encodedString = packet.toByteString

    assert(packet.isInstanceOf[Ipv4Packet])
    assert(payload.isInstanceOf[Generic])
    assert(payload.asInstanceOf[Generic].protocol == Icmp)
    assert(genericString == encodedString)
  }

  it should "create a generic IPv6 payload" in {
    val genericString = "6000000000563A40FE80000000000000020A000A000A000AFF02000000000000000000000000001A9B010DD11EF1030008F00000FD000000000000000218001800180018020607000002030002200102011C0000011802100010001000100210001000100010020F000F000F000F040E00080C0A038000800001001E003C"
    val in = ByteBuffer.wrap(HexFormat.of.parseHex(genericString))
    val header = Ipv6Header.decode(in)
    val payload = Ipv6Payload.decode(in, true, header)
    val packet: IpPacket = new Ipv6Packet(header, payload)
    val encodedString = HexFormat.of.formatHex(packet.toBytes).toUpperCase

    assert(packet.isInstanceOf[Ipv6Packet])
    assert(payload.isInstanceOf[Generic])
    assert(genericString == encodedString)
  }
}
